mapper.select:
(SELECT id, title COLLATE utf8mb4_general_ci as title, description COLLATE utf8mb4_general_ci as description, video_path COLLATE utf8mb4_general_ci as videoPath, video_url COLLATE utf8mb4_general_ci as videoUrl, cover_path COLLATE utf8mb4_general_ci as coverPath, cover_url COLLATE utf8mb4_general_ci as coverUrl, duration, category COLLATE utf8mb4_general_ci as category, user_id as userId, uploader_name COLLATE utf8mb4_general_ci as uploaderName, views, likes, status, create_time as createTime, update_time as updateTime, tags COLLATE utf8mb4_general_ci as tags FROM video
AND (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
) UNION ALL (SELECT id, title COLLATE utf8mb4_general_ci as title, description COLLATE utf8mb4_general_ci as description, video_path COLLATE utf8mb4_general_ci as videoPath, video_url COLLATE utf8mb4_general_ci as videoUrl, cover_path COLLATE utf8mb4_general_ci as coverPath, cover_url COLLATE utf8mb4_general_ci as coverUrl, duration, category COLLATE utf8mb4_general_ci as category, user_id as userId, uploader_name COLLATE utf8mb4_general_ci as uploaderName, views, likes, status, create_time as createTime, update_time as updateTime, tags COLLATE utf8mb4_general_ci as tags FROM draft_video
AND (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
)
SELECT id, title, description, video_path as videoPath, video_url as videoUrl, cover_path as coverPath, cover_url as coverUrl, duration, category, user_id as userId, uploader_name as uploaderName, views, likes, status, create_time as createTime, update_time as updateTime, tags FROM draft_video
status = #{status}
AND (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
SELECT id, title, description, video_path as videoPath, video_url as videoUrl, cover_path as coverPath, cover_url as coverUrl, duration, category, user_id as userId, uploader_name as uploaderName, views, likes, status, create_time as createTime, update_time as updateTime, tags FROM video
status = #{status}
AND (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY createTime DESC LIMIT #{offset}, #{limit}

select.if:
SELECT (SELECT COUNT(*) FROM video
WHERE (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
) + (SELECT COUNT(*) FROM draft_video
WHERE (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
)

choose.when:
SELECT COUNT(*) FROM draft_video

    status = #{status}

        AND (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))

mapper.update:
UPDATE draft_video SET status = #{status},
reject_reason = #{rejectReason},
update_time = #{updateTime} WHERE id = #{id}
